import numpy as npclass NBodySim():    def __innit__(self, bounds, ballr, ballICS, N):                self.bounds = bounds        self.r = ballr        self.ballICS = ballICS        self.N=N        self.partICS= np.zeros((N,2,2))            def radomizeICS(self):        for i in range(self.N):            self.partICS[i][0][0]=np.random.uniform(low = 0, high = self.bounds[0])            self.partICS[i][0][1]=np.random.uniform(low = 0, high = self.bounds[1])            self.partICS[i][1][0]=np.random.normal()            self.partICS[i][1][1]=np.random.normal()            